<!DOCTYPE html>
<html lang="zh-CN">

<head>
    <meta charset="UTF-8">
    <title>仪表盘</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.0/css/all.min.css" />
    @section('styles')
    @show
</head>

<body class="bg-gray-100 text-gray-900">
    <div class="flex h-screen">
        @section('sidebar')
        <!-- 左侧导航 -->
        <aside x-data="sidebar" x-init="init" class="h-full bg-white shadow flex flex-col transition-all duration-300 flex-shrink-0" :class="collapsed ? 'w-20' : 'w-60'">
            <!-- 顶部 Logo + 收缩按钮 -->
            <div class="flex items-center justify-between h-16 border-b px-4">
                <span x-show="!collapsed" class="text-2xl font-bold text-black transition-opacity duration-300">管理后台</span>
                <button @click="toggle()" class="p-1 rounded hover:bg-gray-100 text-gray-700">
                    <svg x-show="!collapsed" xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none"
                        viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M19 12H5" />
                    </svg>
                    <svg x-show="collapsed" xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" fill="none"
                        viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M5 12h14" />
                    </svg>
                </button>
            </div>

            <!-- 菜单列表 -->
            <nav class="flex-1 overflow-y-auto mt-4">
                <ul class="space-y-1">
                    <!-- 仪表盘 -->
                    <li>
                        <a href="/dashboard"
                            class="flex items-center px-4 py-2 text-gray-700 hover:bg-gray-100 rounded transition-colors duration-200"
                            :class="{'bg-gray-100 font-semibold': window.location.pathname === '/dashboard'}">
