            <header class="bg-white shadow p-4 flex justify-between items-center">
                <!-- 页面标题 -->
                @section('title')
                <h1 class="text-2xl font-bold">帖子审核</h1>
                @show

                <!-- 用户信息 -->
                <div class="relative" x-data="{ open: false }">
                    <button @click="open = !open" class="flex items-center space-x-2 focus:outline-none">
                        <!-- <img :src="user.avatar" alt="头像" class="w-8 h-8 rounded-full border">
                        <span x-text="user.name" class="font-semibold"></span> -->
                    </button>

                    <!-- 下拉菜单 -->
                    <div x-show="open" @click.away="open = false"
                        class="absolute right-0 mt-2 w-40 bg-white border rounded shadow z-10">
                        <a href="/profile" class="block px-4 py-2 hover:bg-gray-100">个人设置</a>
                        <button @click="logout" class="w-full text-left px-4 py-2 hover:bg-gray-100">退出登录</button>
                    </div>
                </div>
            </header>
            <div class="p-6 space-y-6" style="min-width: 0;">
                @section('content')
                @show
            </div>
        </div>
    </div>
    <script defer src="https://cdn.jsdelivr.net/npm/alpinejs@3.15.0/dist/cdn.min.js"></script>
    <script>
        document.addEventListener('alpine:init', () => {
            Alpine.data('sidebar', () => ({
                collapsed: false,
                init() {
                    console.log('init')
                    this.highlightCurrent();
                },
                toggle() {
                    this.collapsed = !this.collapsed;
                },
                highlightCurrent() {
                    const path = window.location.pathname;
                    const links = document.querySelectorAll('aside nav ul li a');
                    links.forEach(link => {
                        if (link.getAttribute('href') === path) {
                            link.classList.add('bg-gray-100', 'font-semibold');
                        } else {
                            link.classList.remove('bg-gray-100', 'font-semibold');
                        }
                    });
                }
            }));

            Alpine.data('userInfo', () => ({
                user: {
                    name: '加载中...',
                    avatar: ''
                },
                async fetchUser() {
                    try {
                        const res = await fetch('/api/me');
                        this.user = await res.json();
                    } catch (e) {
                        console.error('获取用户信息失败', e);
                    }
                },
                async logout() {
                    await fetch('/api/logout', {
                        method: 'POST'
                    });
                    window.location.href = '/login'; // 跳转到登录页
                }
            }));


        })
    </script>
    <script>

    </script>
    @section('scripts')
    @show
</body>

</html>